import React, {Component} from "react";

export default class CreateUser extends Component{

    constructor(props){
        super(props);

        this.onChangeUsername = this.onChangeUsername.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            username: ''
        }
    }
    onChangeUsername(e){
        this.setState({
                username: e.target.value
            });
    }
    onSubmit(e){
        e.preventDefault();
        const user = {
            username: this.state.username
        };

        console.log(user);
        this.setState({username: ''})
    }
    render(){
        return(
            <div>
            <h3>Create New User</h3>
                <form onSubmit={this.onSubmit}>

                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" className="form-control" value={this.state.username} 
                        onChange={this.onChangeUsername}/>
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text" className="form-control" value={this.state.description} 
                        onChange={this.onChangeDescription}/>
                    </div>
                    <div className="form-group">
                        <label>Duration: </label>
                        <input type="text" className="form-control" value={this.state.duration} 
                        onChange={this.onChangeDuration}/>
                    </div>
                    <div className="form-group">
                        <label>Date: </label>
                       <div className="col-md-12 col-lg-12 col-sm-12">
                           <DatePicker 
                            selected = {this.state.date}
                            onChange={this.onChangeDate}
                           />
                       </div>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create Exercise" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        );
    }
}
